sys_script_include.script:
var totalCost = Class.create();
totalCost.prototype = {
	initialize: function (mrEventSysid) {
		this.mrEventSysid = mrEventSysid;
	},

	getCost: function () {
		var cost = 0;
		//Get the total cost of all equipment for this event

		var equipment = new GlideRecord(x_1316055_event_ma_equipment_request);
		equipment.addQuery('markething_event', this.mrEventSysid);
		equipment.query();
		while (equipment.next()) {
			cost = cost + parseFloat(equipment.cost.getCurrencyValue());
		}
		return cost;
	},
	type: 'totalCost'
};
